DROP TABLE IF EXISTS `g2monitor_news`;
CREATE TABLE `g2monitor_news` (
  `news_id` varchar(32) NOT NULL COMMENT '主键ID',
  `news_title` varchar(100) NOT NULL COMMENT '标题',
  `publish_time` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP COMMENT '发布时间',
  `view_num` int(11) DEFAULT '0' COMMENT '浏览次数',
  `news_type` tinyint(4) NOT NULL COMMENT '新闻类型（1：交管通告 2:规划政策 3：本地要闻）',
  `news_body` longtext,
  `refs_id` varchar(15) NOT NULL,
  `source` varchar(15) NOT NULL COMMENT '来源（1：交管局 2：物流办）',
  `create_time` timestamp NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`news_id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

